mapper.update:
update tbl_book
   set lend='불가', lendnum=lendnum+1
 where bookno=#{bookno}

update tbl_member
   set totallend=totallend+1
 where userno=#{userno}

update tbl_book
   set lend='가능'
 where bookno=#{bookno}

update tbl_loan
   set status='반납완료'
 where userno=#{userno} and bookno=#{bookno}
