mapper.update:
update tbl_book
   set booktitle=#{booktitle}, author=#{author}, publisher=#{publisher},
   		price=#{price}, page=#{page}, bookintro=#{bookintro},
   		bookindex=#{bookindex}, authorintro=#{authorintro} 
 where bookno=#{bookno}

mapper.select:
select * from tbl_book b left join tbl_attach a
    on b.bookno = a.bookno
 where b.bookno = #{bookno}
